mapper.select:
select
id, table_code, name, value, disabled, width, fixed, parent_id, type, type_type, select_url, align, sortable,
filters, show_search, placeholder, sort
from table_dynamic


        and id = #{id}


        and table_code = #{tableCode}


        and name = #{name}


        and value = #{value}


        and disabled = #{disabled}


        and width = #{width}


        and fixed = #{fixed}


        and parent_id = #{parentId}


        and type = #{type}


        and type_type = #{typeType}


        and select_url = #{selectUrl}


        and align = #{align}


        and sortable = #{sortable}


        and filters = #{filters}


        and show_search = #{showSearch}


        and placeholder = #{placeholder}


        and sort = #{sort}

select count(1)
from table_dynamic


        and id = #{id}


        and table_code = #{tableCode}


        and name = #{name}


        and value = #{value}


        and disabled = #{disabled}


        and width = #{width}


        and fixed = #{fixed}


        and parent_id = #{parentId}


        and type = #{type}


        and type_type = #{typeType}


        and select_url = #{selectUrl}


        and align = #{align}


        and sortable = #{sortable}


        and filters = #{filters}


        and show_search = #{showSearch}


        and placeholder = #{placeholder}


        and sort = #{sort}
